{
    "tiddlers": {
        "$:/plugins/DoolPlex/TiddlyKanban/TKMacro": {
            "created": "20220103195706481",
            "creator": "Joost van den Dool",
            "text": "\define set-value(field, value)
<$action-setfield $tiddler=<<actionTiddler>> $field="$field$" $value=$value$/>
\end

\define render-card(tiddler)
<$draggable tiddler="$tiddler$" >
<li class="kanban-card">
$tiddler$
</li>
</$draggable>
\end


\define KanbanBoard(columns, query, actionfield)
<div class="kanban-board">
<$list filter="$columns$">
<$droppable actions=<<set-value field:$actionfield$ value:{{!!title}}>> >
<ol class="kanban-lane">
<h2><$view field="title" /></h2>
<$list filter="[$query$field:$actionfield${!!title}]">
<$macrocall $name="render-card" tiddler=<<currentTiddler>> />
</$list>
<br /><br />
</ol></$droppable>
</$list>
</div>
\end",
            "tags": "$:/tags/Macro",
            "title": "$:/plugins/DoolPlex/TiddlyKanban/TKMacro",
            "modified": "20220103202003332",
            "modifier": "Joost van den Dool",
            "type": "text/vnd.tiddlywiki",
            "module-type": "macro"
        },
        "$:/plugins/DoolPlex/TiddlyKanban/TKStyles": {
            "created": "20220103195855045",
            "creator": "Joost van den Dool",
            "text": " .kanban-board {
                display: flex;
                flex-direction: row;
                justify-content: space-evenly;
                align-items: stretch;
            }

            ol.kanban-lane {
  flex: 1;
                background-color: #d3d3d3;
color: #000000;
                margin: 2px 5px 2px 5px;
                padding: 0.6rem;
            }

            li.kanban-card {
                list-style: none;
                background-color: #ffffff;
color: #000000;

                margin: 2px 2px 2px 2px;
                padding: 5px 5px 5px 5px;
                border: 0.1rem solid black;
            }",
            "tags": "$:/tags/Stylesheet",
            "title": "$:/plugins/DoolPlex/TiddlyKanban/TKStyles",
            "modified": "20220104081444220",
            "modifier": "Joost van den Dool",
            "type": "text/css"
        }
    }
}
